import React, { Component } from 'react';

import ViewButton from './ViewButton';
import { deleteStock } from '../actions/stockActions';
import { connect } from 'react-redux';
class StockCard extends Component {

  handleDelete = (e) => {
    e.preventDefault()

      console.log(e.target.name)
      this.props.deleteStock(this.props.stock.id)

    // Message(e.target.name)
  }

  render() {

    return (
      <div style={{"margin": "auto",
      "width": "20%",
      "padding": "10px",
      "color": "green"}} key={this.props.stock.id}>
        <h3>{this.props.stock.name}</h3>
        <h5>Amount:{this.props.stock.amount}</h5>
        <ViewButton stock={this.props.stock}/>
        <button name={this.props.stock.name} id={this.props.stock.id} onClick={(e) => this.handleDelete(e)} >DELETE</button>

      </div>
    );
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
      deleteStock: (id) => dispatch(deleteStock(id)),
      removeStock: (id) => dispatch(remove)
  }
}

export default connect(null,mapDispatchToProps)(StockCard);
