export const fetchStocks = () => {
    return (dispatch) => {
        // dispatch({type:'LOADING_STOCKS'})
       fetch("http://localhost:3000/stocks")
        .then(resp => {return resp.json()})
        .then(resp => {dispatch({type:'LOAD_STOCKS', stocks: resp})})
    }
}

export const sendStock = (stockInfo) => {
    console.log(stockInfo.namepa)
    const stock = {stock:{name: stockInfo.name+ '',
    amount: stockInfo.amount+ '',
    purchase_price: stockInfo.purchasePrice+ '',
    selling_price: stockInfo.sellingPrice+ ''}}
    const options =  {
        // mode: "no-cors",
        method: "POST",
        credentials: "include",
        headers: {
            "Content-Type": "application/json"
            // "Accept": "application/json"
        },
        body: JSON.stringify({stock})
    }
    return (dispatch) => {
        console.log(options.body)
        fetch("http://localhost:3000/stocks",options).then(resp => resp.json())
          .then(data => data)
          dispatch(fetchStocks())

    }
}
