          <AddStock />
          <button onClick={this.handleClick}>Click Me!</button><span style={{"margin": "auto",
      "width": "30%",
      "padding": "20px",
      "color": "white"}}>{this.state.count}</span>


          {this.props.stocks.map(stock => <StockCard key={stock.id} stock={stock}/>)}

      </div>
    );
  }
}
const mapDispatchToProps = (dispatch) => {
