export default function manageStocks(state = {
  stocks: [], loading: false
}, action) {
      switch (action.type) {
        case 'LOADING_STOCKS':
          return {
            ...state,
            stocks: [...state.stocks],
            loading:true
          }
        case 'ADD_STOCKS':
          return {
            ...state,
            stocks: action.stocks,
            loading:false
          }
        case 'GET_STOCK':
          console.log(action)
         let stock = state.stocks.filter(stock=>stock.id == action.id)
         return {
           ...state,
            stocks
         }
         return stocks.push(stock)


        default:
          return state;
      }

  }
